import React, {Component} from 'react';
import NavBar from './components/NavBar';
import { BrowserRouter, Switch } from 'react-router-dom';
import Homepage from './components/Homepage'
import About from './components/About';
import RecipeList from './components/RecipeList';
import Register from './components/Register';
import * as contentful from 'contentful';
import './App.css';
import {Route} from 'react-router';
import SingleRecipe from './components/SingleRecipe';
import Footer from './components/Footer';
import UserForm from './components/UserForm';
import PageNotFound from './components/PageNotFound'
import ContactUs from './components/ContactUs'
import LanguageSwitcher from './LanguageSwitcher'
import { useTranslation } from 'react-i18next';


const SPACE_ID = 'fb7egqp4t5o4';
const ACCESS_TOKEN = 'gTzwtGt5x7av4xXQZQSYo11eWMR166TQwzMSpPE0bVc';

const client = contentful.createClient({
  space: SPACE_ID,
  accessToken: ACCESS_TOKEN
});

const { t, i18n } = useTranslation();

class App extends Component {

  constructor(props) {
    super(props)
    this.state = {
      recipes: [],
    }
  }

  componentDidMount() {
      client.getEntries({
          content_type: 'recipe',
      })
      .then((response => {
          this.setState({recipes: response.items})
      }))
      .catch((error) => {
          console.log("Error occurred fetching data");
          console.log(error)
      })
  }


  handleClick(lang) {
    i18n.changeLanguage(lang);
  }


  render() {
    // generate a route for each item in the array
    let recipeRoutes = [];
    let registerRoutes = [];
    this.state.recipes.forEach((item, i) => {
      recipeRoutes.push(<Route path={`/recipes/${item.fields.slug}`} component={SingleRecipe} key={i} />);
      registerRoutes.push(<Route path={`/register/${item.fields.slug}`} component={UserForm} key={i} />);
    });

      return (
        <div>
          <BrowserRouter>
            <div className="App">
              <NavBar/>
              <button onClick={()=>this.handleClick('en')}>
                English
              </button>
              <button onClick={()=>this.handleClick('en')}>
                French
              </button>
              {/* <LanguageSwitcher/> */}
              <Switch>
                  <Route path="/" exact component={Homepage}/>
                  <Route path="/about" component={About}/>
                  <Route path="/register" exact component={Register}/>
                  <Route path="/recipes" exact component={RecipeList}/>
                  <Route path="/contactUs" component={ContactUs}/>
                  {recipeRoutes}
                  {registerRoutes}
                  <Route component={PageNotFound} />
              </Switch>
            </div>
          </BrowserRouter>
          <Footer/>
        </div>
      )
  }
}

export default App;
