import React from 'react';
import NavBar from './components/NavBar';
import { BrowserRouter, Router } from 'react-router-dom';
import Homepage from './components/Homepage'
import About from './components/About';
import RecipeList from './components/RecipeList';
import Register from './components/Register';
import * as contentful from 'contentful';

import './App.css';
import {Route} from 'react-router';

import SingleRecipe from './components/SingleRecipe';
import {createBrowserHistory} from 'history';
const browserHistory = createBrowserHistory();

const SPACE_ID = 'fb7egqp4t5o4';
const ACCESS_TOKEN = 'gTzwtGt5x7av4xXQZQSYo11eWMR166TQwzMSpPE0bVc';

function App() {

  const client = contentful.createClient({
    space: SPACE_ID,
    accessToken: ACCESS_TOKEN
  });

  client.getEntries({
    content_type: 'recipe',
  })
  .then((response => {
    let recipes = response.items;
    // generate a route for each item in the content array
    let recipeRoutes = [];
    recipes.forEach((item, i) => {
      recipeRoutes.push(<Route path={`/lessons/${item.title}`} component={SingleRecipe} key={i} />);
    });
  })).catch((error) => {
    console.log("Error occurred fetching data");
    console.log(error)
})




    return (
      <div>
        <BrowserRouter>
          <div className="App">
            <NavBar/>
            <Route path="/" exact component={Homepage}/>
            <Route path="/about" component={About}/>
            <Route path="/register" component={Register}/>
            <Route path="/recipes" component={RecipeList}/>
            <Route exact path="/lessons/:slug" component={SingleRecipe} />

          </div>
        </BrowserRouter>
      </div>
    );
}

export default App;
