        }
        return instructions;
    }

    render() {

        const recipe = this.props.history.location.state.recipe.fields
        console.log(recipe)
        // console.log(recipe.instructions.content[0].content[0].content[0].content[0].value)

        // var instructions = this.createTable(recipe);

        return (
            <div>
                <h1>{recipe.title}</h1>
                <h3>Video Instructions</h3>
                <video id="background-video" loop controls>
                <source src={recipe.video.fields.file.url} type="video/mp4" />
                Your browser does not support the video tag.
                </video>
                <h3>Written Instructions</h3>
                <div>{this.createTable(recipe)}</div>


            </div>
        )
    }

}

export default SingleRecipe;
