import React from 'react';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import * as contentful from 'contentful';

const SPACE_ID = 'fb7egqp4t5o4';
const ACCESS_TOKEN = 'gTzwtGt5x7av4xXQZQSYo11eWMR166TQwzMSpPE0bVc';

const client = contentful.createClient({
    space: SPACE_ID,
    accessToken: ACCESS_TOKEN
});

var state = {
    recipes: [],
    searchString: ''
}



const RecipePage = (props) => {

    var lesson = client.getEntries({
        content_type: 'recipe',
        query: this.state.searchString
    })
    .then((response => {
        this.setState({recipes: response.items})
    }))
    .catch((error) => {
        console.log("Error occurred fetching data");
        console.log(error)
    });

    console.log(lesson);



    return(
        <div>
            { props.recipe ? (
                <Card>
                    <CardMedia style={{height: 0, paddingTop: '56.25%'}}
                    image={props.recipe.fields.image.fields.file.url}
                    title={props.recipe.fields.title}/>
                    <CardContent>
                        <Typography gutterBottom variant="h4" component="h4">
                            {props.recipe.fields.title}
                        </Typography>
                        <Typography component="p">
                            <b>Instructor: </b> {props.recipe.fields.chef}
                        </Typography>
                        <br/>
                        <Typography component="p">
                            {props.recipe.fields.description}
                        </Typography>
                        <Typography component="p">
                            {props.recipe.fields.video}
                        </Typography>
                        <Typography component="p">
                            {props.recipe.fields.instructions}
                        </Typography>
                    </CardContent>
                </Card>
            ): null}
        </div>
    )
}

export default RecipePage;
