import React from 'react';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import { BrowserRouter, Route } from 'react-router-dom';
import RecipePage from './RecipePage'


const Recipe = (props) => {
    var recipeUrl = props.recipe.fields.title.replace(/\s+/g, '');

    const handleOnClick = e => {
        props.history.push({
          pathname: `/explore/${props.recipe.fields.title}`,
          state: { content: props.content }
        });
      }


    return(
        <div>
            { props.recipe ? (
                <Card>
                    <CardMedia style={{height: 0, paddingTop: '56.25%'}}
                    image={props.recipe.fields.image.fields.file.url}
                    title={props.recipe.fields.title}/>
                    <CardContent>
                        <Typography gutterBottom variant="h4" component="h4">
                            {props.recipe.fields.title}
                        </Typography>
                        <Typography component="p">
                            <b>Instructor: </b> {props.recipe.fields.chef}
                        </Typography>
                        <br/>
                        <Typography component="p">
                            {props.recipe.fields.description}
                        </Typography>
                    </CardContent>
                    <CardActions style={{display: 'flex', alignItems: 'center', justifyContent: 'space-between'}}>



                        <Button size="small" color="primary" component={RecipePage} href={recipeUrl} target="_blank">
                            Go to Recipe
                        </Button>
                        <Button size="small" color="primary" target="_blank">
                            Register for Class
                        </Button>
                    </CardActions>
                </Card>
            ): null}
        </div>
    )
}

export default Recipe;
