import React from 'react';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import ReactDOM from 'react-dom';
import { BrowserRouter, Route, Switch, Router } from 'react-router-dom'

import SingleRecipe from './SingleRecipe';
import {createBrowserHistory} from 'history';
import {Link} from 'react-router-dom'

const browserHistory = createBrowserHistory();

function RecipeListItem(props) {

    const {chef, date, description, image, slug, title} = props.recipe;

    const handleOnClick = e => {
        props.history.push({
          pathname: `/${props.recipe.fields.slug}`,
          state: { recipe: props, history: props.history }
        });
      }


    return(
        <div>
                {console.log(props)}

            { props.recipe ? (

                <Card>
                    <CardMedia style={{height: 0, paddingTop: '56.25%'}}
                    image={props.recipe.fields.image.fields.file.url}
                    title={props.recipe.fields.title}/>
                    <CardContent>
                        <Typography gutterBottom variant="h4" component="h4">
                            {props.recipe.fields.title}
                        </Typography>
                        <Typography component="p">
                            <b>Instructor: </b> {props.recipe.fields.chef}
                        </Typography>
                        <br/>
                        <Typography component="p">
                            {props.recipe.fields.description}
                        </Typography>
                    </CardContent>
                    <CardActions style={{display: 'flex', alignItems: 'center', justifyContent: 'space-between'}}>

                    <Link to={`lessons/${props.recipe.fields.slug}`}>
                        <Button size="small" color="primary" target="_blank">
                        Go to Recipe
                        </Button>
                    </Link>
                    <Route path={`lessons/:slug`} component={SingleRecipe} exact />

                        {/* <BrowserRouter>
                            <Route path="/lessons/:slug" render={(props) => <SingleRecipe {...props} />} />
                        </BrowserRouter> */}


                    {/* <Router history={browserHistory}>
                        <Route path='lessons/:slug' component={SingleRecipe}/>
                    </Router> */}


                        <Button size="small" color="primary" target="_blank">
                            Register for Class
                        </Button>
                    </CardActions>
                </Card>
            ): null}
        </div>
    )
}

export default RecipeListItem;
