import React, {Component} from 'react';
import CardMedia from '@material-ui/core/CardMedia';

class SingleRecipe extends Component {
    constructor(props) {
        super(props)
        this.state = {recipe: null}
    }

    render() {

        const recipe = this.props.history.location.state.recipe.fields
        console.log(recipe)
    console.log(recipe.instructions.content[0].content)

        var instructions = []

        // Outer loop to create parent
        for (let i = 0; i < recipe.instructions.content[0].content; i++) {
          let children = []

          //Inner loop to create children
          for (let i = 0; i < recipe.instructions.content[0].content; i++) {
            children.push(<div>{recipe.instructions.content[0].content[i]}</div>)
          }

          //Create the parent and add the children
          instructions.push(<tr>{children}</tr>)
        }

        console.log(instructions)


        return (
            <div>
                <h1>{recipe.title}</h1>
                <h3>Video Instructions</h3>
                <video id="background-video" loop controls>
                <source src={recipe.video.fields.file.url} type="video/mp4" />
                Your browser does not support the video tag.
                </video>
                <h3>Written Instructions</h3>
                <div>{instructions}</div>


            </div>
        )
    }

}

export default SingleRecipe;
