import React from 'react';
import Card from '@material-ui/core/Card';
import CardActions from '@material-ui/core/CardActions';
import CardContent from '@material-ui/core/CardContent';
import CardMedia from '@material-ui/core/CardMedia';
import Button from '@material-ui/core/Button';
import Typography from '@material-ui/core/Typography';
import history from './history';

import {Router, IndexRoute, Route, Switch} from 'react-router';
import SingleRecipe from './SingleRecipe';
import {createBrowserHistory} from 'history';
import Link from '@material-ui/core/Link'

const browserHistory = createBrowserHistory();

const RecipeListItem = (props) => {


    return(
        <div>
            { props.recipe ? (

                <Card>
                <h2><Link href={`lessons/${props.recipe.fields.slug}`}>{props.recipe.fields.title}</Link></h2>

                    <CardMedia style={{height: 0, paddingTop: '56.25%'}}
                    image={props.recipe.fields.image.fields.file.url}
                    title={props.recipe.fields.title}/>
                    <CardContent>
                        <Typography gutterBottom variant="h4" component="h4">
                            {props.recipe.fields.title}
                        </Typography>
                        <Typography component="p">
                            <b>Instructor: </b> {props.recipe.fields.chef}
                        </Typography>
                        <br/>
                        <Typography component="p">
                            {props.recipe.fields.description}
                        </Typography>
                    </CardContent>
                    <CardActions style={{display: 'flex', alignItems: 'center', justifyContent: 'space-between'}}>


                    <Router>
                <Switch>
                <Route path="/" exact render={() => {
                    return (
                        <div className="FormCenter">
                            <form className="FormFields" onSubmit={this.handleSubmit}>
                                <div className="FormField">
                                    <label className="FormField__Label" htmlFor="cnic">CNIC</label>
                                    <input type="text" id="cnic" className="FormField__Input" placeholder="Enter your CNIC without dashes" name="cnic"/>
                                </div>
                                <div className="FormField">
                                    <Link to={{
                                        pathname: '/SupervisorDashboard'
                                    }} className="FormField__Button mr-20">Sign In</Link>

                                </div>
                            </form>
                        </div>
                    );
                }}/>
                <Route exact path="/SupervisorDashboard" component={SingleRecipe} />
                </Switch>
            </Router>



                        <Button size="small" color="primary" onClick={() => history.push(`lessons/${props.recipe.fields.slug}`)} target="_blank">
                            Go to Recipe
                        </Button>

                    <Router history={browserHistory}>
                        <Route path='lessons/:slug' component={SingleRecipe}/>
                    </Router>


                        <Button size="small" color="primary" target="_blank">
                            Register for Class
                        </Button>
                    </CardActions>
                </Card>
            ): null}
        </div>
    )
}

export default RecipeListItem;
